mapper.select:
select * From TLESSON Where 1=1
And NAME like CONCAT('%','${NAME}','%' )
Order By STARTTIME asc limit #{pageBean.startRowNum},#{pageBean.rows}

mapper.update:
	   update TLESSON set  NAME = #{NAME},
	   					STARTTIME = #{STARTTIME},
	   					ENDTIME = #{ENDTIME},
                        ISACTIVES = #{ISACTIVES}
        where  ID=#{ID}

mapper.delete:
	    delete From  TLESSON   Where 1 = 1
	    And ID in 
	       
           #{id}  
        ;
        delete From  TTIMELESSON   Where 1 = 1
	    And LESSONID in 
	       
           #{id}  
        ;
