mapper.select:
select * From TLESSON Where 1=1
And NAME like CONCAT('%','${NAME}','%' )
limit #{pageBean.startRowNum},#{pageBean.rows}

select * From  TLESSON 
Where 1=1

	And ISACTIVES =#{ISACTIVES}

mapper.update:
	   update TLESSON set  NAME = #{NAME},
	   					STARTTIME = #{STARTTIME},
	   					ENDTIME = #{ENDTIME},
                        ISACTIVES = #{ISACTIVES}
        where  ID=#{ID}

mapper.delete:
	    delete From  TLESSON   Where 1 = 1
	    And ID in 
	       
           #{id}  
        ;
