mapper.select:
select *,(Select NAME From TBUSER Where ID=a.USERID) USERNAME , (Select FORMAT(count(*)/2,1) From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID)) MUCHLESSON, (Select min(DATE) From TTEACHERTIME Where ID In (Select TIMEID From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID))) FIRST, (Select max(DATE) From TTEACHERTIME Where ID In (Select TIMEID From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID))) LATEST From TSEMESTER a Where 1=1
And USERID In (Select ID From TBUSER Where NAME like CONCAT('%','${XSNAME}','%' ) )
And USERID =#{USERID}
And ID In (Select SEMESTERID From TSEMESTERELECTIVE Where ELECTIVEID In (Select ELECTIVEID From TELECTIVELESSON Where TIMEID In (Select ID From TTEACHERTIME Where USERID=#{LSUSERID})))
limit #{pageBean.startRowNum},#{pageBean.rows}

select a.*,b.*,b.ID XSUSERID,a.ID SEMESTERID, (Select count(*)/2 From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID)) ZLESSON, (Select count(*)/2 From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID) And ID In (Select ELID From TCLASSEL Where CLASSID In (Select ID From TCLASS Where XSUSERID=b.ID And STATUS=1))) YDLESSON, (Select min(DATE) From TTEACHERTIME Where ID In (Select TIMEID From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID))) FIRST, (Select max(DATE) From TTEACHERTIME Where ID In (Select TIMEID From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID))) LATEST From TSEMESTER a join TBUSER b on a.USERID=b.ID Where 1=1
And USERID In (Select ID From TBUSER Where NAME like CONCAT('%','${XSNAME}','%' ) )
And USERID =#{XSUSERID}
And a.ID In (Select SEMESTERID From TSEMESTERELECTIVE Where ELECTIVEID In (Select ELECTIVEID From TELECTIVELESSON Where TIMEID In (Select ID From TTEACHERTIME Where USERID=#{LSUSERID})))
limit #{pageBean.startRowNum},#{pageBean.rows}

Select count(*) From TCLASS Where 1=1 And STATUS is not null and ID in (select CLASSID From TCLASSEL Where ELID In (Select ID From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID in
#{id}
)))

mapper.delete:
	    delete From  TSEMESTER   Where 1 = 1
	    And ID in 
	       
           #{id}  
        ;

	    delete From  TCLASS   Where 1 = 1
	    And ID in (select CLASSID From TCLASSEL Where ELID In 
	    (Select ID From TELECTIVELESSON Where ELECTIVEID In 
	    (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID in
	        
           #{id}  
        
        )))

		delete From  TELECTIVE   Where 1 = 1
	    And ID in   (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID in
	         
           #{id}  
        
        );
        delete From  TELECTIVELESSON   Where 1 = 1
	    And ELECTIVEID in (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID in
	         
           #{id}  
        
        );
