mapper.select:
select *,(Select NAME From TBUSER Where ID=a.USERID) USERNAME , (Select count(*) From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID)) MUCHLESSON, (Select min(DATE) From TTEACHERTIME Where ID In (Select TIMEID From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID))) FIRST, (Select max(DATE) From TTEACHERTIME Where ID In (Select TIMEID From TELECTIVELESSON Where ELECTIVEID In (Select ELECTIVEID From TSEMESTERELECTIVE Where SEMESTERID = a.ID))) LATEST From TSEMESTER a Where 1=1
And USERID In (Select ID From TBUSER Where NAME like CONCAT('%','${XSNAME}','%' ) )
And USERID =#{USERID}
And ID In (Select SEMESTERID From TSEMESTERELECTIVE Where ELECTIVEID In (Select ELECTIVEID From TELECTIVELESSON Where TIMEID In (Select ID From TTEACHERTIME Where USERID=#{LSUSERID})))
limit #{pageBean.startRowNum},#{pageBean.rows}

mapper.delete:
	    delete From  TSEMESTER   Where 1 = 1
	    And ID in 
	       
           #{id}  
        ;
